import React, { useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import StarIcon from '@mui/icons-material/Star';
import Data from './ReviewD';


const Review = () => {
  useEffect(() => {
    // Initialize Owl Carousel
    const owl = jQuery('.owl-carousel');
    owl.owlCarousel({
      loop: true,
      margin: 10,
      responsive: {
        0: {
          items: 1,
        },
        600: {
          items: 2,
        },
        1000: {
          items: 3,
        },
      },
    });

    // Destroy Owl Carousel on component unmount
    return () => {
      owl.trigger('destroy.owl.carousel');
    };
  }, []); // Empty dependency array to ensure it runs once on mount

  return (
    <Container className="mt-5">
      <h5 className="fn-20 text-center">Reviews</h5>
      <Row className="mt-5">
        <Col>
          <OwlCarousel className="owl-theme">
            {Data.map((val) => (
              <div key={val.id} className="item-card text-center text-white" style={{ width: '300px' }}>
                <img src={val.User} className="rounded-circle border border-4 border-warning mx-auto" style={{ maxWidth: '120px' }} alt={val.name} />

                <div className="item-card-content bg-danger px-3 py-5 text-white mt-n1" style={{ boxShadow: '10px 10px grey' }}>
                  <h5 className="mt-3 card-title pb-2 mb-4 border-warning border-bottom border-2 ">{val.name}</h5>
                  <div>
                    <StarIcon className="text-warning" />
                    <StarIcon />
                  </div>
                  <p className="mb-0 fs-6">{val.review}</p>
                </div>
              </div>
            ))}
          </OwlCarousel>
        </Col>
      </Row>
    </Container>
  );
};

export default Review;
