import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import StarIcon from '@mui/icons-material/Star';
import im2 from './imges/testimonial-image01.jpg';
const Review = () => {
  const reviews = [
    {
      id: 1,
      name: 'Mohan Yadav',
      image: im2,
      rating: 4,
      review: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
    },
    // Add more reviews as needed
  ];

  // Owl Carousel Settings
  const options = {
    loop: true,
    items: 1,
    margin: 10,
    nav: false,
    dots: true,
    autoplay: true,
    autoplayTimeout: 5000,
    autoplayHoverPause: true,
  };

  return (
    <Container className="mt-5">
      <Row className="mt-5">
        <Col>
          <OwlCarousel className="owl-theme" {...options}>
            {reviews.map((review) => (
              <div key={review.id} className="item-card text-center text-white">
                <img src={review.image} className="rounded-circle border border-4 border-warning mx-auto" style={{ maxWidth: '120px' }} alt={review.name} />

                <div className="item-card-content bg-danger px-3 py-5 text-white mt-n1" style={{ boxShadow: '10px 10px grey' }}>
                  <h5 className="mt-3 card-title pb-2 mb-4 border-warning border-bottom border-2 ">{review.name}</h5>
                  <div>
                    {Array.from({ length: review.rating }).map((_, index) => (
                      <StarIcon key={index} className="text-warning" />
                    ))}
                  </div>
                  <p className="mb-0 fs-6">"{review.review}"</p>
                </div>
              </div>
            ))}
          </OwlCarousel>
        </Col>
      </Row>
    </Container>
  );
};

export default Review;
