import im2 from "./imges/testimonial-image01.jpg"
import StarIcon from '@mui/icons-material/Star';
import Data from './Img'
function Review(){
    return(<Container className="mt-5">
<Row className="mt-5">
<h5 className="fn-20 text-center">Reviews</h5>
    {
        Data.map((val)=>{
            <Col>
<div className="item-card text-center text-white" style={{width:"300px"}}>
<img src={val.User} className="rounded-circle border border-4 border-warning mx-auto" style={{maxWidth:"120px"}}/>

<div className="item-card-content bg-danger px-3 py-5 text-white mt-n1" style={{boxShadow:"10px 10px grey"}}>
<h5 className="mt-3 card-title pb-2 mb-4 border-warning border-bottom border-2 ">{val.name}</h5>
<div>
    <StarIcon className="text-warning"/>
    <StarIcon/>
</div>
    <p className="mb-0 fs-6">"Lorem ipsum dolor sit amet, consectetur Lorem ipsum dolor sit amet, consectetur "</p>
</div>
</div>

    </Col>
        })
    }
</Row>

    </Container>)
    
}
