import React from 'react';
import TestiMonialsDetails from './TestiMonialDetail'; // Corrected import
import OwlCarousel from 'react-owl-carousel';

const TestiMonials = () => {
  const testiMonials = [
    // Your testimonial data here...
  ];

  // Owl Carousel Settings
  const options = {
    loop: true,
    center: true,
    items: 3,
    margin: 0,
    autoplay: true,
    dots: true,
    autoplayTimeout: 8500,
    smartSpeed: 450,
    nav: false,
    responsive: {
      0: {
        items: 1,
      },
      600: {
        items: 3,
      },
      1000: {
        items: 3,
      },
    },
  };

  return (
    <section id="testimonial" className="testimonials pt-70 pb-70">
      <div className="container mt-5">
        <h4 className="miniTitle text-center">TESTIMONIALS</h4>
        <div className="text-center">
          <h3 className="sectionTitle">What Our Clients are Saying?</h3>
        </div>
        <p className="text-center">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna.
        </p>
        <div className="row">
          <div className="col-md-12">
            <OwlCarousel className="owl-carousel owl-theme" {...options}>
              {testiMonials.length === 0 ? (
                <div className="item">
                  <div className="shadow-effect">
                    <img className="img-circle" alt="text image" />

                    <p>
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna.
                    </p>
                  </div>
                  <div className="testimonial-name">
                    <h5>Rajon Rony</h5>
                    <small>ITALY</small>
                  </div>
                </div>
              ) : (
                testiMonials.map((testiMonialDetail, index) => (
                  <TestiMonialsDetails testiMonialDetail={testiMonialDetail} key={index} />
                ))
              )}
            </OwlCarousel>
          </div>
        </div>
      </div>
    </section>
  );
};

export default TestiMonials;
